command:example.dev:code:
$app = Get-RavelloApplication -ApplicationName App1

if(!(Test-RavelloApplicationPublished -ApplicationId $app.id))
{
   $app | Publish-RavelloApplication -PreferredCloud GOOGLE -PreferredRegion europe-west1 `
      -OptimizationLevel COST_OPTIMIZED -StartAllVM:$false
}

Set-RavelloApplicationVm -ApplicationName App1 -VmName VM1 -NewName NewVM1 -NewDescription 'Changed Name'
if(Test-RavelloApplicationPendingUpdate -ApplicationName App1)
{
  Publish-RavelloApplication -ApplicationName App1
}

Set-RavelloApplicationVm -ApplicationName App1 -VmName VM1 -NewName NewVM1 -NewDescription 'Changed Name'
if(Test-RavelloApplicationPendingUpdate -ApplicationName App1)
{
  Publish-RavelloApplication -ApplicationName App1
}

Set-RavelloApplicationVmIso -ApplicationName App1 -VmName VM1 -DiskImageName Disk1
if(Test-RavelloApplicationPendingUpdate -ApplicationName ThinApp)
{
  Publish-RavelloApplication -ApplicationName ThinApp
}

$order = $order = @{
  Name = 'Group1'
  DelaySeconds = 300
  VM = 'VM1','VM2'
},@{
  Name = 'Group2'
  DelaySeconds = 0
  VM = 'VM3'
}
New-RavelloApplicationOrderGroup -ApplicationName App1 -StartOrder $order
